select from dingdanxinxi where 1=1 and dingdanhao like concat('%',#{dingdanhao,jdbcType=VARCHAR},'%') and dingdanjine like concat('%',#{dingdanjine,jdbcType=VARCHAR},'%') and dingdanneirong like concat('%',#{dingdanneirong,jdbcType=VARCHAR},'%') and yonghuming like concat('%',#{yonghuming,jdbcType=VARCHAR},'%') and shouji like concat('%',#{shouji,jdbcType=VARCHAR},'%') and dizhi like concat('%',#{dizhi,jdbcType=VARCHAR},'%') and beizhu like concat('%',#{beizhu,jdbcType=VARCHAR},'%')
order by id desc
limit #{pageno},#{pageSize}
