mapper.select:
select from shangpinxinxi where 1=1 and shangpinbianhao like concat('%',#{shangpinbianhao,jdbcType=VARCHAR},'%') and shangpinmingcheng like concat('%',#{shangpinmingcheng,jdbcType=VARCHAR},'%') and leibie like concat('%',#{leibie,jdbcType=VARCHAR},'%') and xiaoliang >= concat('',#{xiaoliang1,jdbcType=VARCHAR},'') and xiaoliang <= concat('',#{xiaoliang2,jdbcType=VARCHAR},'') and kucun >= concat('',#{kucun1,jdbcType=VARCHAR},'') and kucun <= concat('',#{kucun2,jdbcType=VARCHAR},'') and jiage >= concat('',#{jiage1,jdbcType=VARCHAR},'') and jiage <= concat('',#{jiage2,jdbcType=VARCHAR},'') and tupian like concat('%',#{tupian,jdbcType=VARCHAR},'%') and shangpinjianjie like concat('%',#{shangpinjianjie,jdbcType=VARCHAR},'%') and faburen like concat('%',#{faburen,jdbcType=VARCHAR},'%')
order by id desc
limit #{pageno},#{pageSize}
